import React from 'react';
import axios from 'axios';
import utils from 'util'

import pager from '../assets/Pager-Rsz.png'
export default class Register extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            email: '',
            usrname: '',
        };
        this.changeUsrHandler = this.changeUsrHandler.bind(this);
        this.changeEmailHandler = this.changeEmailHandler.bind(this);
        this.sumbitHandler = this.sumbitHandler.bind(this);
        var usrData = new FormData();

    }


    changeUsrHandler(event) {
        this.setState({ usrname: event.target.value });
    }

    changeEmailHandler(event) {
        this.setState({ email: event.target.value });
    }

    // AXIOS POST METHOD 

    // axios.post('/api/register/', this.state).then(function(res){
    //     console.log(res);
    //     console.log(res.data);
    // });

    sumbitHandler(event) {


        usrData.append(this.state.usrname, this.state.email);

        axios({
            method: "POST",
            headers: 'Access-Control-Allow-Origin',
            mode: 'cors',
            url: "http://localhost:5000/api/add",
            data: usrData,
            config: { headers: { 'Content-Type': 'multipart/form-data' } }

        }).then(function (response) {
            //handle success
            alert('Thank you ' + this.state.usrname + ' your email has been submitted ('
            + this.state.email + ')');
            console.log(response);
        })
            .catch(function (response) {
                //handle error
                alert('Sorry ' + this.state.usrname + ' there has been an issue registering your details..');
                console.log(response);
            })

        event.preventDefault();
    }



    // --------------------------------------


    render() {

        return (
            <div id="register-wrapper" >
                <div>
                    <img src={pager} alt="Captains Pager" />
                    <form onSubmit={this.sumbitHandler}>

                        <input type="text"
                            value={this.state.usrname}
                            placeholder=""
                            onChange={this.changeUsrHandler} />

                        <input type="text"
                            value={this.state.email}
                            placeholder=""
                            onChange={this.changeEmailHandler} />

                        <input type="submit"
                            value="Submit"
                            className="BtnLarge submit" />
                    </form>
                </div>

                <div>
                    <h1 id="pager"> <span> Sign up to get a message from Captain Stranges pager.</span> </h1>
                </div>

            </div>
        )
    }
};


